import React, { useEffect, useState } from 'react';
import NoteList from '../components/NoteList';
import { Note } from '../types/Note';
import { api } from '../services/api';
import './NotesPage.css';

const NotesPage: React.FC = () => {
    const [notes, setNotes] = useState<Note[]>([]);
    const [isLoading, setIsLoading] = useState(true);
    const [error, setError] = useState<string | null>(null);
    const [searchTerm, setSearchTerm] = useState('');
    const [selectedCategory, setSelectedCategory] = useState('');
    const [sortBy, setSortBy] = useState('date-desc');

    useEffect(() => {
        const fetchNotes = async () => {
            try {
                const fetchedNotes = await api.getNotes();
                console.log('Fetched notes:', fetchedNotes);
                setNotes(fetchedNotes);
            } catch (err) {
                setError('Failed to fetch notes');
                console.error('Error fetching notes:', err);
            } finally {
                setIsLoading(false);
            }
        };

        fetchNotes();
    }, []);

    // Get unique categories from notes
    const categories = [...new Set(notes.map(note => note.category))].filter(Boolean);

    // Filter and sort notes
    const filteredNotes = notes.filter(note => {
        const matchesSearch = note.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
                            note.content.toLowerCase().includes(searchTerm.toLowerCase());
        const matchesCategory = !selectedCategory || note.category === selectedCategory;
        return matchesSearch && matchesCategory;
    });

    return (
        <div className="notes-page">
            <h1>My Notes</h1>
            <div className="search-filters">
                <input
                    type="text"
                    placeholder="Search notes..."
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                    className="search-input"
                />
                <select
                    value={selectedCategory}
                    onChange={(e) => setSelectedCategory(e.target.value)}
                    className="category-filter"
                >
                    <option value="">All Categories</option>
                    {categories.map(category => (
                        <option key={category} value={category}>
                            {category}
                        </option>
                    ))}
                </select>
                <select
                    value={sortBy}
                    onChange={(e) => setSortBy(e.target.value)}
                    className="sort-select"
                >
                    <option value="date-desc">Newest First</option>
                    <option value="date-asc">Oldest First</option>
                    <option value="title">Title</option>
                    <option value="category">Category</option>
                </select>
            </div>
            <NoteList 
                notes={filteredNotes}
                isLoading={isLoading}
                error={error}
                onNoteSelect={(note: Note) => console.log('Selected note:', note)}
                onDeleteNote={(id: number) => console.log('Delete note:', id)}
                onMakePublic={(id: number) => console.log('Make public:', id)}
                onShare={(id: number) => console.log('Share note:', id)}
            />
        </div>
    );
};

export default NotesPage;
