import React from 'react';
import { Note } from '../types/Note';
import './NoteList.css';

interface NoteListProps {
    notes: Note[];
    selectedNote?: Note;
    sharedNotes?: Note[];
    showSharedSection?: boolean;
    onNoteSelect: (note: Note) => void;
    onDeleteNote: (noteId: number) => Promise<void>;
    onMakePublic: (noteId: number) => Promise<void>;
    onShare: (noteId: number) => void;
}

const NoteList: React.FC<NoteListProps> = ({
    notes,
    selectedNote,
    sharedNotes,
    showSharedSection = false,
    onNoteSelect,
    onDeleteNote,
    onMakePublic,
    onShare
}) => {
    return (
        <div className="note-lists-container">
            {/* Only show My Notes section if there are notes */}
            {notes.length > 0 && (
                <section className="my-notes-section">
                    <div className="notes-grid">
                        {notes.map((note) => (
                            <div 
                                key={note.id} 
                                className={`note-card ${selectedNote?.id === note.id ? 'selected' : ''}`}
                            >
                                <div className="note-content" onClick={() => onNoteSelect(note)}>
                                    <h3>{note.title}</h3>
                                    <p>{note.content.substring(0, 100)}...</p>
                                    <small>Category: {note.category}</small>
                                </div>
                                <div className="note-actions">
                                    <button onClick={() => onDeleteNote(note.id)}>Delete</button>
                                    <button onClick={() => onShare(note.id)}>Share</button>
                                    {!note.isPublic && (
                                        <button onClick={() => onMakePublic(note.id)}>Make Public</button>
                                    )}
                                </div>
                            </div>
                        ))}
                    </div>
                </section>
            )}

            {/* Only show Shared Notes section if showSharedSection is true */}
            {showSharedSection && sharedNotes && sharedNotes.length > 0 && (
                <section className="shared-notes-section">
                    <div className="notes-grid">
                        {sharedNotes.map((note) => (
                            <div 
                                key={note.id} 
                                className="note-card shared"
                            >
                                <div className="note-content" onClick={() => onNoteSelect(note)}>
                                    <h3>{note.title}</h3>
                                    <p>{note.content.substring(0, 100)}...</p>
                                    <div className="note-metadata">
                                        <small>Category: {note.category}</small>
                                        <small>Shared by: {note.user?.username}</small>
                                    </div>
                                </div>
                            </div>
                        ))}
                    </div>
                </section>
            )}

            {/* Show message when no notes are available */}
            {notes.length === 0 && (!showSharedSection || !sharedNotes || sharedNotes.length === 0) && (
                <p className="no-notes-message">No notes available</p>
            )}
        </div>
    );
};

export default NoteList;
