import React, { useState, useEffect } from 'react';
import './App.css';
import NoteList from './components/NoteList';
import NoteEditor from './components/NoteEditor';
import Login from './components/auth/Login';
import Register from './components/auth/Register';
import { useAuth } from './contexts/AuthContext';
import { Note } from './types/Note';

type SortOption = 'date-desc' | 'date-asc' | 'title' | 'category';

function App() {
    const { user, logout } = useAuth();
    const [showRegister, setShowRegister] = useState(false);
    const [notes, setNotes] = useState<Note[]>(() => {
        const savedNotes = localStorage.getItem('notes');
        return savedNotes ? JSON.parse(savedNotes) : [];
    });
    const [selectedNote, setSelectedNote] = useState<Note | undefined>(undefined);
    const [searchTerm, setSearchTerm] = useState('');
    const [selectedCategory, setSelectedCategory] = useState<string>('');
    const [sortBy, setSortBy] = useState<SortOption>('date-desc');

    useEffect(() => {
        localStorage.setItem('notes', JSON.stringify(notes));
    }, [notes]);

    // Dacă utilizatorul nu este autentificat, afișăm formularul de login/register
    if (!user) {
        return (
            <div className="App">
                <header className="App-header">
                    <h1>Notepad+</h1>
                </header>
                <main className="auth-container">
                    {showRegister ? (
                        <>
                            <Register />
                            <p>
                                Ai deja cont?{' '}
                                <button onClick={() => setShowRegister(false)}>
                                    Autentifică-te
                                </button>
                            </p>
                        </>
                    ) : (
                        <>
                            <Login />
                            <p>
                                Nu ai cont?{' '}
                                <button onClick={() => setShowRegister(true)}>
                                    Înregistrează-te
                                </button>
                            </p>
                        </>
                    )}
                </main>
            </div>
        );
    }

    // Restul codului existent pentru aplicația autentificată
    const handleSaveNote = (noteData: Partial<Note>) => {
        if (selectedNote) {
            const updatedNotes = notes.map(note =>
                note.id === selectedNote.id
                    ? { ...note, ...noteData, updatedAt: new Date() }
                    : note
            );
            setNotes(updatedNotes);
        } else {
            const newNote: Note = {
                id: Date.now(),
                title: noteData.title || '',
                content: noteData.content || '',
                category: noteData.category || '',
                createdAt: new Date(),
                updatedAt: new Date(),
                userId: user.id
            };
            setNotes([...notes, newNote]);
        }
        setSelectedNote(undefined);
    };

    // ... restul funcțiilor existente ...

    return (
        <div className="App">
            <header className="App-header">
                <h1>Notepad+</h1>
                <div className="user-info">
                    <span>Bine ai venit, {user.username}!</span>
                    <button onClick={logout} className="logout-button">
                        Deconectare
                    </button>
                </div>
            </header>
            {/* Restul componentei rămâne la fel */}
            <div className="search-filters">
                {/* ... codul existent ... */}
            </div>
            <main className="App-main">
            <NoteList 
    notes={filteredAndSortedNotes} 
    onNoteSelect={setSelectedNote}
    onDeleteNote={handleDeleteNote}
    selectedNote={selectedNote}
/>
                <NoteEditor 
                    note={selectedNote}
                    onSave={handleSaveNote}
                />
            </main>
        </div>
    );
}

export default App;NoteList
