import React from 'react';
import { Note, NoteStatus } from '../types/Note';
import './NoteList.css';

interface NoteListProps {
    notes: Note[];
    selectedNote?: Note;
    onNoteSelect: (note: Note) => void;
    onDeleteNote: (id: number) => void;
    onMakePublic: (id: number) => void;
    onShare: (id: number) => void;
    viewType?: string;
    isLoading?: boolean;
    error?: string | null;
}

const NoteList: React.FC<NoteListProps> = ({ 
    notes, 
    onNoteSelect, 
    onDeleteNote, 
    onMakePublic,
    onShare,
    isLoading,
    error 
}) => {
    if (isLoading) return <div>Loading...</div>;
    if (error) return <div className="error">{error}</div>;

    return (
        <div className="notes-grid">
            {notes.map(note => (
                <div key={note.id} className="note-card" onClick={() => onNoteSelect(note)}>
                    <div className="note-header">
                        <h3 className="note-title">{note.title}</h3>
                        <span className={`note-status ${note.status.toLowerCase()}`}>
                            {note.status}
                        </span>
                    </div>
                    <p className="note-preview">{note.content}</p>
                    <div className="note-footer">
                        <span className="note-category">Category: {note.category}</span>
                        <div className="note-actions">
                            <button 
                                className="delete-btn"
                                onClick={(e) => {
                                    e.stopPropagation();
                                    onDeleteNote(note.id);
                                }}
                            >
                                Delete
                            </button>
                            <button 
                                className="share-btn"
                                onClick={(e) => {
                                    e.stopPropagation();
                                    onShare(note.id);
                                }}
                            >
                                Share
                            </button>
                            {note.status !== NoteStatus.Public && (
                                <button 
                                    className="public-btn"
                                    onClick={(e) => {
                                        e.stopPropagation();
                                        onMakePublic(note.id);
                                    }}
                                >
                                    Make Public
                                </button>
                            )}
                        </div>
                    </div>
                </div>
            ))}
        </div>
    );
};

export default NoteList;
