import React, { useState, useEffect } from 'react';
import './App.css';
import NoteList from './components/NoteList';
import NoteEditor from './components/NoteEditor';
import Login from './components/auth/Login';
import Register from './components/auth/Register';
import { useAuth } from './contexts/AuthContext';
import { Note } from './types/Note';

type SortOption = 'date-desc' | 'date-asc' | 'title' | 'category';

function App() {
    const { user, logout } = useAuth();
    const [showRegister, setShowRegister] = useState(false);
    const [notes, setNotes] = useState<Note[]>(() => {
        const savedNotes = localStorage.getItem('notes');
        return savedNotes ? JSON.parse(savedNotes) : [];
    });
    const [selectedNote, setSelectedNote] = useState<Note | undefined>(undefined);
    const [searchTerm, setSearchTerm] = useState('');
    const [selectedCategory, setSelectedCategory] = useState<string>('');
    const [sortBy, setSortBy] = useState<SortOption>('date-desc');

    useEffect(() => {
        localStorage.setItem('notes', JSON.stringify(notes));
    }, [notes]);

    const sortNotes = (notesToSort: Note[]): Note[] => {
        return [...notesToSort].sort((a, b) => {
            switch (sortBy) {
                case 'date-desc':
                    return new Date(b.updatedAt).getTime() - new Date(a.updatedAt).getTime();
                case 'date-asc':
                    return new Date(a.updatedAt).getTime() - new Date(b.updatedAt).getTime();
                case 'title':
                    return a.title.localeCompare(b.title);
                case 'category':
                    return a.category.localeCompare(b.category);
                default:
                    return 0;
            }
        });
    };

    const handleDeleteNote = async (noteId: number) => {
        if (window.confirm('Sigur doriți să ștergeți această notiță?')) {
            setNotes(notes.filter(note => note.id !== noteId));
            if (selectedNote?.id === noteId) {
                setSelectedNote(undefined);
            }
        }
    };

    const handleSaveNote = (noteData: Partial<Note>) => {
        if (selectedNote) {
            const updatedNotes = notes.map(note =>
                note.id === selectedNote.id
                    ? { ...note, ...noteData, updatedAt: new Date() }
                    : note
            );
            setNotes(updatedNotes);
        } else {
            const newNote = {
                id: Date.now(),
                title: noteData.title || '',
                content: noteData.content || '',
                category: noteData.category || '',
                createdAt: new Date(),
                updatedAt: new Date(),
                userId: user!.id
            };
            
            if (!user) {
                console.error('Cannot create note: No user found');
                return;
            }
            
            setNotes([...notes, newNote]);
        }
        setSelectedNote(undefined);
    };

    const filteredAndSortedNotes = sortNotes(
        notes.filter(note => {
            const matchesSearch = note.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
                                note.content.toLowerCase().includes(searchTerm.toLowerCase());
            const matchesCategory = !selectedCategory || note.category === selectedCategory;
            return matchesSearch && matchesCategory;
        })
    );

    if (!user) {
        return (
            <div className="App">
                <header className="App-header">
                    <h1>Notepad+</h1>
                </header>
                <main className="auth-container">
                    {showRegister ? (
                        <>
                            <Register />
                            <p>
                                Ai deja cont?{' '}
                                <button onClick={() => setShowRegister(false)}>
                                    Autentifică-te
                                </button>
                            </p>
                        </>
                    ) : (
                        <>
                            <Login />
                            <p>
                                Nu ai cont?{' '}
                                <button onClick={() => setShowRegister(true)}>
                                    Înregistrează-te
                                </button>
                            </p>
                        </>
                    )}
                </main>
            </div>
        );
    }

    return (
        <div className="App">
            <header className="App-header">
                <h1>Notepad+</h1>
                <div className="user-info">
                    <span>Bine ai venit, {user.username}!</span>
                    <button onClick={logout} className="logout-button">
                        Deconectare
                    </button>
                </div>
            </header>
            <div className="search-filters">
                <input
                    type="text"
                    placeholder="Caută notițe..."
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                    className="search-input"
                />
                <select
                    value={selectedCategory}
                    onChange={(e) => setSelectedCategory(e.target.value)}
                    className="category-filter"
                >
                    <option value="">Toate categoriile</option>
                    {Array.from(new Set(notes.map(note => note.category))).map(category => (
                        <option key={category} value={category}>
                            {category}
                        </option>
                    ))}
                </select>
                <select
                    value={sortBy}
                    onChange={(e) => setSortBy(e.target.value as SortOption)}
                    className="sort-select"
                >
                    <option value="date-desc">Cele mai noi</option>
                    <option value="date-asc">Cele mai vechi</option>
                    <option value="title">După titlu</option>
                    <option value="category">După categorie</option>
                </select>
            </div>
            <main className="App-main">
                <NoteList 
                    notes={filteredAndSortedNotes}
                    onNoteSelect={setSelectedNote}
                    onDeleteNote={handleDeleteNote}
                    selectedNote={selectedNote}
                />
                <NoteEditor 
                    note={selectedNote}
                    onSave={handleSaveNote}
                />
            </main>
        </div>
    );
}

export default App;
