import { api } from './api';

export interface Notification {
    id: number;
    message: string;
    type: 'NewPublicNote' | 'NoteShared';
    noteId?: number;
    createdAt: string;
    isRead: boolean;
    userId: number;
}

export const notificationService = {
    getNotifications: async (): Promise<Notification[]> => {
        try {
            const response = await api.axiosInstance.get<{ data: Notification[] }>('/notifications');
            return response.data.data || [];
        } catch (error) {
            console.error('Error fetching notifications:', error);
            return [];
        }
    },

    markAsRead: async (id: number): Promise<void> => {
        try {
            await api.axiosInstance.put(`/notifications/${id}/read`);
        } catch (error) {
            console.error('Error marking notification as read:', error);
            throw error;
        }
    },

    markAllAsRead: async (): Promise<void> => {
        try {
            await api.axiosInstance.put('/notifications/read-all');
        } catch (error) {
            console.error('Error marking all notifications as read:', error);
            throw error;
        }
    }
};
