import { Note, User, NoteApiResponse, NoteStatus, convertApiResponseToNote } from '../types/Note';
import { LoginRequest, RegisterRequest } from '../types/Auth';

const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';

// Update the user type to match C# casing
interface CurrentUser {
    Id: number;  // Changed from id to Id
    Username: string;  // Changed from username to Username
}

// Export getCurrentUser function
export const getCurrentUser = (): User | null => {
    try {
        const userJson = localStorage.getItem('user');
        if (!userJson) return null;

        const rawUser = JSON.parse(userJson);
        if (!rawUser) return null;

        // Convert PascalCase to camelCase
        return {
            id: rawUser.Id || rawUser.id,
            username: rawUser.Username || rawUser.username,
            email: rawUser.Email || rawUser.email,
            createdAt: new Date(rawUser.CreatedAt || rawUser.createdAt)
        };
    } catch {
        return null;
    }
};

// Add at the top of the file with other utility functions
const getToken = (): string | null => {
    const user = getCurrentUser();
    if (!user) return null;
    // If you store the token separately in localStorage
    return localStorage.getItem('token');
};

export const api = {
    // Auth operations
    login: async (credentials: LoginRequest): Promise<User> => {
        const response = await fetch(`${API_URL}/auth/login`, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(credentials),
        });

        if (!response.ok) {
            const error = await response.text();
            throw new Error(error || 'Login failed');
        }

        const userData = await response.json();
        
        // Format the user data to handle both casings
        const formattedUser = {
            id: userData.id || userData.Id,
            username: userData.username || userData.Username,
            email: userData.email || userData.Email,
            createdAt: new Date(userData.createdAt || userData.CreatedAt),
            // Keep the original properties for backward compatibility
            Id: userData.Id || userData.id,
            Username: userData.Username || userData.username,
            Email: userData.Email || userData.email,
            CreatedAt: userData.CreatedAt || userData.createdAt
        };

        // Store the formatted user data in localStorage
        localStorage.setItem('user', JSON.stringify(formattedUser));
        return formattedUser;
    },

    register: async (userData: RegisterRequest): Promise<User> => {
        const response = await fetch(`${API_URL}/auth/register`, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(userData),
        });
        if (!response.ok) {
            const error = await response.text();
            throw new Error(error || 'Registration failed');
        }
        return response.json();
    },

    // Note operations
    getNotes: async (): Promise<Note[]> => {
        const user = getCurrentUser();
        if (!user?.id) {
            throw new Error('User not authenticated');
        }

        try {
            console.log('Fetching notes for user:', user.id);
            const response = await fetch(`${API_URL}/notes`, {
                method: 'GET',
                headers: {
                    'Content-Type': 'application/json',
                    'Accept': 'application/json',
                    'UserId': user.id.toString()
                }
            });

            if (!response.ok) {
                throw new Error(`HTTP error! status: ${response.status}`);
            }

            const apiNotes: NoteApiResponse[] = await response.json();
            console.log('Received notes:', apiNotes);
            
            if (!Array.isArray(apiNotes)) {
                console.error('Expected array of notes but got:', apiNotes);
                return [];
            }

            return apiNotes.map(convertApiResponseToNote);
        } catch (error) {
            console.error('Error fetching notes:', error);
            throw error;
        }
    },

    createNote: async (note: Partial<Note>): Promise<Note> => {
        const user = getCurrentUser();
        if (!user?.Id) {
            throw new Error('User not authenticated');
        }

        try {
            const response = await fetch(`${API_URL}/notes`, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    'Accept': 'application/json',
                    'UserId': user.Id.toString()
                },
                body: JSON.stringify({
                    title: note.title || '',
                    content: note.content || '',
                    category: note.category || '',
                    status: NoteStatus.Personal,
                    isPublic: false
                })
            });

            if (!response.ok) {
                const error = await response.json();
                throw new Error(error.message || 'Failed to create note');
            }

            const apiNote: NoteApiResponse = await response.json();
            return convertApiResponseToNote(apiNote);
        } catch (error) {
            console.error('Create note error:', error);
            throw error;
        }
    },

    updateNote: async (id: number, note: Partial<Note>): Promise<Note> => {
        try {
            const response = await fetch(`${API_URL}/notes/${id}`, {
                method: 'PUT',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({
                    id,
                    title: note.title || '',
                    content: note.content || '',
                    category: note.category || '',
                    userId: note.userId
                }),
            });

            if (!response.ok) {
                const error = await response.text();
                throw new Error(error || 'Failed to update note');
            }

            const updatedNote = await response.json();
            return {
                ...updatedNote,
                createdAt: new Date(updatedNote.createdAt),
                updatedAt: new Date(updatedNote.updatedAt)
            };
        } catch (err) {
            console.error('Update error:', err);
            throw err;
        }
    },

    deleteNote: async (id: number): Promise<void> => {
        const response = await fetch(`${API_URL}/notes/${id}`, {
            method: 'DELETE',
        });
        if (!response.ok) {
            const error = await response.text();
            throw new Error(error || 'Failed to delete note');
        }
    },

    shareNote: async (noteId: number, collaboratorId: number): Promise<void> => {
        const user = getCurrentUser();
        if (!user?.Id) {  // Changed from id to Id
            throw new Error('User not authenticated');
        }

        const response = await fetch(`${API_URL}/notes/share`, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
                'UserId': user.Id.toString()  // Changed from id to Id
            },
            body: JSON.stringify({
                noteId,
                collaboratorId
            }),
        });

        if (!response.ok) {
            const error = await response.json();
            throw new Error(error.message || 'Failed to share note');
        }
    },

    getSharedNotes: async (): Promise<Note[]> => {
        const user = getCurrentUser();
        if (!user?.Id) {  // Changed from id to Id
            throw new Error('User not authenticated');
        }

        const response = await fetch(`${API_URL}/notes/shared`, {
            headers: {
                'Content-Type': 'application/json',
                'UserId': user.Id.toString()  // Changed from id to Id
            }
        });
        
        if (!response.ok) {
            const error = await response.json();
            throw new Error(error.message || 'Failed to fetch shared notes');
        }
        
        const notes = await response.json();
        return notes.map((note: any) => ({
            ...note,
            createdAt: new Date(note.createdAt),
            updatedAt: new Date(note.updatedAt)
        }));
    },

    makeNotePublic: async (id: number): Promise<void> => {
        const user = getCurrentUser();
        if (!user?.Id) {  // Changed from id to Id
            throw new Error('User not authenticated');
        }

        const response = await fetch(`${API_URL}/notes/${id}/make-public`, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
                'UserId': user.Id.toString()  // Changed from id to Id
            },
        });

        if (!response.ok) {
            const error = await response.json();
            throw new Error(error.message || 'Failed to make note public');
        }
    },

    searchUsers: async (searchTerm: string): Promise<User[]> => {
        const user = getCurrentUser();
        if (!user?.Id) {
            throw new Error('User not authenticated');
        }

        console.log('Starting user search with term:', searchTerm);
        console.log('Current user:', user);

        try {
            // Use lowercase 'users' in the URL
            const url = new URL(`${API_URL}/users/search`);
            url.searchParams.append('term', searchTerm);
            console.log('Request URL:', url.toString());

            const headers = {
                'Content-Type': 'application/json',
                'Accept': 'application/json',
                'UserId': user.Id.toString()
            };
            console.log('Request headers:', headers);

            // Test the controller first
            const pingResponse = await fetch(`${API_URL}/users/ping`);
            console.log('Ping response:', await pingResponse.text());

            const response = await fetch(url.toString(), {
                method: 'GET',
                headers
            });

            console.log('Response status:', response.status);
            console.log('Response headers:', Object.fromEntries(response.headers.entries()));
            
            const responseText = await response.text();
            console.log('Raw response text:', responseText);

            if (!response.ok) {
                console.error('Response not OK:', {
                    status: response.status,
                    statusText: response.statusText,
                    body: responseText
                });
                throw new Error(responseText || `HTTP error! status: ${response.status}`);
            }

            if (!responseText.trim()) {
                console.warn('Empty response received');
                return [];
            }

            try {
                const data = JSON.parse(responseText);
                console.log('Parsed response data:', data);

                if (!data.users) {
                    console.error('Response missing users array:', data);
                    return [];
                }

                const mappedUsers = data.users.map((user: any) => ({
                    id: user.id,
                    username: user.username,
                    email: user.email,
                    createdAt: new Date(user.createdAt)
                }));
                console.log('Mapped users:', mappedUsers);
                return mappedUsers;

            } catch (parseError) {
                console.error('JSON parse error:', parseError);
                throw new Error('Invalid JSON response from server');
            }
        } catch (error) {
            console.error('Search users error:', {
                error,
                message: error instanceof Error ? error.message : 'Unknown error'
            });
            throw error instanceof Error ? error : new Error('Failed to search users');
        }
    },

    getPublicNotes: async (): Promise<Note[]> => {
        const user = getCurrentUser();
        if (!user?.Id) {
            throw new Error('User not authenticated');
        }

        try {
            // Try with explicit URL
            const url = `${API_URL}/notes/public`;
            console.log('Requesting public notes from:', url);

            const response = await fetch(url, {
                method: 'GET',
                headers: {
                    'Accept': 'application/json',
                    'UserId': user.Id.toString()
                }
            });

            // Log full response details
            console.log('Response:', {
                status: response.status,
                statusText: response.statusText,
                headers: Object.fromEntries(response.headers.entries())
            });

            if (!response.ok) {
                throw new Error(`HTTP error! status: ${response.status}`);
            }

            const data = await response.json();
            console.log('Received data:', data);
            return data;
        } catch (error) {
            console.error('Error fetching public notes:', error);
            throw error;
        }
    },

    getSharedWithMeNotes: async (): Promise<Note[]> => {
        const user = getCurrentUser();
        if (!user?.Id) {
            throw new Error('User not authenticated');
        }

        console.log('Fetching shared notes for user:', user.Id);

        const response = await fetch(`${API_URL}/notes/shared`, {
            method: 'GET',
            headers: {
                'Content-Type': 'application/json',
                'Accept': 'application/json',
                'UserId': user.Id.toString()
            }
        });
        
        if (!response.ok) {
            const errorText = await response.text();
            console.error('Failed to fetch shared notes:', errorText);
            throw new Error('Failed to fetch shared notes');
        }
        
        const notes = await response.json();
        console.log('Received shared notes:', notes);

        return notes.map((note: any) => ({
            ...note,
            createdAt: new Date(note.createdAt || note.CreatedAt),
            updatedAt: new Date(note.updatedAt || note.UpdatedAt),
