import React, { createContext, useContext, useState, useEffect } from 'react';
import { User } from '../types/Note';
import { api } from '../services/api';

interface AuthContextType {
    user: User | null;
    login: (username: string, password: string) => Promise<void>;
    logout: () => void;
    register: (username: string, email: string, password: string) => Promise<void>;
}
// În funcția login și register
localStorage.setItem('token', userData.token || '');

const AuthContext = createContext<AuthContextType | undefined>(undefined);

export const AuthProvider: React.FC<{ children: React.ReactNode }> = ({ children }) => {
    const [user, setUser] = useState<User | null>(null);

    useEffect(() => {
        // Verifică dacă există un token salvat
        const token = localStorage.getItem('token');
        if (token) {
            // Implementează logica de verificare a tokenului
        }
    }, []);

    const login = async (username: string, password: string) => {
        try {
            const userData = await api.login({ username, password });
            // Assuming userData contains both user info and token
            if ('token' in userData) {
                const { token, ...userDataWithoutToken } = userData as User & { token: string };
                setUser(userDataWithoutToken);
                localStorage.setItem('token', token);
            } else {
                setUser(userData);
            }
        } catch (error) {
            console.error('Login failed:', error);
            throw error;
        }
    };

    const logout = () => {
        setUser(null);
        localStorage.removeItem('token');
    };

    const register = async (username: string, email: string, password: string) => {
        try {
            const userData = await api.register({ username, email, password });
            // Check if token exists in response
            if ('token' in userData) {
                const { token, ...userDataWithoutToken } = userData as User & { token: string };
                setUser(userDataWithoutToken);
                localStorage.setItem('token', token);
            } else {
                setUser(userData);
            }
        } catch (error) {
            console.error('Registration failed:', error);
            throw error;
        }
    };

    return (
        <AuthContext.Provider value={{ user, login, logout, register }}>
            {children}
        </AuthContext.Provider>
    );
};

export const useAuth = () => {
    const context = useContext(AuthContext);
    if (context === undefined) {
        throw new Error('useAuth must be used within an AuthProvider');
    }
    return context;
};
