import React, { createContext, useContext, useState, useEffect } from 'react';
import { User } from '../types/Note';
import { api } from '../services/api';
import axios from 'axios';

interface AuthContextType {
    user: User | null;
    token: string | null;
    login: (username: string, password: string) => Promise<void>;
    logout: () => void;
    register: (username: string, email: string, password: string) => Promise<void>;
}

const AuthContext = createContext<AuthContextType | undefined>(undefined);

export const AuthProvider: React.FC<{ children: React.ReactNode }> = ({ children }) => {
    const [user, setUser] = useState<User | null>(() => {
        try {
            const storedUser = localStorage.getItem('user');
            console.log('Stored user data:', storedUser); // Debug log
            
            if (!storedUser) return null;
            
            // Validate that it's actually JSON before parsing
            if (typeof storedUser !== 'string' || !storedUser.startsWith('{')) {
                console.warn('Invalid user data in localStorage');
                localStorage.removeItem('user'); // Clean up invalid data
                return null;
            }
            
            const parsedUser = JSON.parse(storedUser);
            if (!parsedUser || typeof parsedUser !== 'object') {
                console.warn('Parsed user data is not an object');
                return null;
            }
            
            return parsedUser;
        } catch (error) {
            console.error('Error parsing stored user:', error);
            localStorage.removeItem('user'); // Clean up invalid data
            return null;
        }
    });
    const [token, setToken] = useState<string | null>(() => {
        try {
            const storedToken = localStorage.getItem('token');
            console.log('Stored token:', storedToken ? '[PRESENT]' : '[NONE]'); // Debug log
            return storedToken;
        } catch (error) {
            console.error('Error retrieving token:', error);
            return null;
        }
    });

    useEffect(() => {
        // Set up axios interceptor with current token
        const interceptor = axios.interceptors.request.use(
            (config) => {
                if (token && config.headers) {
                    config.headers['Authorization'] = `Bearer ${token}`;
                }
                return config;
            },
            (error) => {
                return Promise.reject(error);
            }
        );

        return () => {
            axios.interceptors.request.eject(interceptor);
        };
    }, [token]);

    useEffect(() => {
        if (user) {
            localStorage.setItem('user', JSON.stringify(user));
        } else {
            localStorage.removeItem('user');
        }
    }, [user]);

    const login = async (username: string, password: string) => {
        try {
            const response = await api.login({ username, password });
            console.log('Login response:', response); // Debug log
            
            if (!response.user || !response.token) {
                throw new Error('Invalid login response format');
            }
            
            setUser(response.user);
            setToken(response.token);
            localStorage.setItem('user', JSON.stringify(response.user));
            localStorage.setItem('token', response.token);
        } catch (error) {
            console.error('Login failed:', error);
            // Clean up any partial data
            setUser(null);
            setToken(null);
            localStorage.removeItem('user');
            localStorage.removeItem('token');
            throw error;
        }
    };

    const register = async (username: string, email: string, password: string) => {
        try {
            const userData = await api.register({ username, email, password });
            setUser(userData);
            localStorage.setItem('user', JSON.stringify(userData));
        } catch (error) {
            console.error('Registration failed:', error);
            throw error;
        }
    };

    const logout = () => {
        setUser(null);
        setToken(null);
        localStorage.removeItem('user');
        localStorage.removeItem('token');
    };

    return (
        <AuthContext.Provider value={{ user, token, login, logout, register }}>
            {children}
        </AuthContext.Provider>
    );
};

export const useAuth = () => {
    const context = useContext(AuthContext);
    if (context === undefined) {
        throw new Error('useAuth must be used within an AuthProvider');
    }
    return context;
};
