import React, { useState, useEffect, useRef } from 'react';
import { DriveFile } from '../types/File';
import { api } from '../services/api';
import './DrivePage.css';

const DrivePage: React.FC = () => {
    const [files, setFiles] = useState<DriveFile[]>([]);
    const [isLoading, setIsLoading] = useState(false);
    const [error, setError] = useState<string | null>(null);
    const fileInputRef = useRef<HTMLInputElement>(null);

    useEffect(() => {
        loadFiles();
    }, []);

    const loadFiles = async () => {
        try {
            setIsLoading(true);
            const fetchedFiles = await api.getFiles();
            setFiles(fetchedFiles);
        } catch (err) {
            setError('Failed to load files');
            console.error(err);
        } finally {
            setIsLoading(false);
        }
    };

    const handleFileUpload = async (event: React.ChangeEvent<HTMLInputElement>) => {
        const file = event.target.files?.[0];
        if (!file) return;

        try {
            setIsLoading(true);
            const response = await api.uploadFile(file);
            setFiles(prev => [...prev, {
                id: response.id,
                name: response.name,
                url: response.url,
                size: file.size,
                type: file.type,
                userId: 0, // Will be set by server
                createdAt: new Date(),
                isPublic: false
            }]);
        } catch (err) {
            setError('Failed to upload file');
            console.error(err);
        } finally {
            setIsLoading(false);
        }
    };

    const handleDelete = async (fileId: number) => {
        try {
            await api.deleteFile(fileId);
            setFiles(files.filter(f => f.id !== fileId));
        } catch (err) {
            setError('Failed to delete file');
            console.error(err);
        }
    };

    const formatFileSize = (bytes: number): string => {
        if (bytes === 0) return '0 Bytes';
        const k = 1024;
        const sizes = ['Bytes', 'KB', 'MB', 'GB'];
        const i = Math.floor(Math.log(bytes) / Math.log(k));
        return parseFloat((bytes / Math.pow(k, i)).toFixed(2)) + ' ' + sizes[i];
    };

    return (
        <div className="drive-page">
            <div className="drive-header">
                <div className="header-left">
                    <h2>My files</h2>
                </div>
                <div className="header-actions">
                    <div className="search-box">
                        <input type="text" placeholder="Search files..." />
                        <span className="search-icon">üîç</span>
                    </div>
                    <button 
                        className="upload-button"
                        onClick={() => fileInputRef.current?.click()}
                    >
                        Upload
                    </button>
                    <button className="create-new-button">
                        Create new +
                    </button>
                </div>
            </div>

            <div className="drive-content">
                {files.length === 0 && !isLoading && (
                    <div className="empty-drive">
                        <div className="empty-icon">üìÅ</div>
                        <h3>Create a folder and upload files</h3>
                        <button 
                            className="create-folder-button"
                            onClick={() => fileInputRef.current?.click()}
                        >
                            Create new folder +
                        </button>
                        <div className="tips">
                            <p>A few tips to start:</p>
                            <ol>
                                <li>Each folder and file has a link</li>
                                <li>Install apps and backup automatically</li>
                                <li>Change the access rights of the link to "Private" or set a password</li>
                                <li>Disable ads by subscribing to PRO</li>
                            </ol>
                        </div>
                    </div>
                )}

                <input
                    type="file"
                    ref={fileInputRef}
                    onChange={handleFileUpload}
                    style={{ display: 'none' }}
                    multiple
                />

                {error && <div className="error-message">{error}</div>}
                
                {isLoading ? (
                    <div className="loading">Loading...</div>
                ) : (
                    <div className="files-list">
                        {files.map(file => (
                            <div key={file.id} className="file-item">
                                <div className="file-icon">
                                    {file.type.startsWith('image/') ? 'üñºÔ∏è' : 'üìÑ'}
                                </div>
                                <div className="file-info">
                                    <div className="file-name">{file.name}</div>
                                    <div className="file-meta">
                                        <span>{formatFileSize(file.size)}</span>
                                        <span>‚Ä¢</span>
                                        <span>{new Date(file.createdAt).toLocaleDateString()}</span>
                                    </div>
                                </div>
                                <div className="file-actions">
                                    <button className="action-button" title="Download">‚¨áÔ∏è</button>
                                    <button className="action-button" title="Share">üîó</button>
                                    <button className="action-button" title="Delete" onClick={() => handleDelete(file.id)}>
                                        üóëÔ∏è
                                    </button>
                                </div>
                            </div>
                        ))}
                    </div>
                )}
            </div>
        </div>
    );
};

export default DrivePage;
