import React, { useState } from 'react';
import { Note, NoteStatus, User } from '../types/Note';
import Modal from './Modal';
import './NoteStatusIndicator.css';

interface NoteStatusIndicatorProps {
    note: Note;
}

export const NoteStatusIndicator: React.FC<NoteStatusIndicatorProps> = ({ note }) => {
    const [showSharedModal, setShowSharedModal] = useState(false);

    // Add type checking and default value
    const status = note.status?.toString() || NoteStatus.Personal;
    const statusClass = `status-indicator ${status.toLowerCase()}`;

    const getStatusText = () => {
        switch (status) {
            case NoteStatus.Public:
                return 'ðŸŒ Public';
            case NoteStatus.Shared:
                return 'ðŸ”„ Shared';
            case NoteStatus.Personal:
                return 'ðŸ”’ Personal';
            default:
                return 'Unknown';
        }
    };

    const handleClick = () => {
        if (status === NoteStatus.Shared) {
            setShowSharedModal(true);
        }
    };

    return (
        <>
            <div 
                className={statusClass}
                title={`Status: ${status}`}
                onClick={handleClick}
                style={{ cursor: status === NoteStatus.Shared ? 'pointer' : 'default' }}
            >
                {getStatusText()}
            </div>

            {showSharedModal && (
                <Modal
                    isOpen={showSharedModal}
                    onClose={() => setShowSharedModal(false)}
                    title="Shared With"
                >
                    <div className="shared-users-list">
                        {note.collaborators?.length ? (
                            <ul>
                                {note.collaborators.map((user: User) => (
                                    <li key={user.id}>
                                        <span className="username">{user.username}</span>
                                        <span className="email">({user.email})</span>
                                    </li>
                                ))}
                            </ul>
                        ) : (
                            <p>No users to display</p>
                        )}
                    </div>
                </Modal>
            )}
        </>
    );
};
