import React, { useState } from 'react';
import { Note } from '../types/Note';
import { ShareNoteDialog } from './ShareNoteDialog';
import './NoteList.css';

interface NoteListProps {
    notes: Note[];
    sharedNotes?: Note[];
    selectedNote?: Note;
    onNoteSelect: (note: Note) => void;
    onDeleteNote: (id: number) => void;
    onMakePublic: (id: number) => void;
}

export const NoteList: React.FC<NoteListProps> = ({
    notes,
    sharedNotes = [],
    selectedNote,
    onNoteSelect,
    onDeleteNote,
    onMakePublic
}) => {
    const [shareDialogNoteId, setShareDialogNoteId] = useState<number | null>(null);

    return (
        <div className="note-list">
            <h2>My Notes</h2>
            {notes.map(note => (
                <div
                    key={note.id}
                    className={`note-item ${selectedNote?.id === note.id ? 'selected' : ''}`}
                    onClick={() => onNoteSelect(note)}
                >
                    <div className="note-title">{note.title}</div>
                    <div className="note-actions">
                        <button onClick={() => setShareDialogNoteId(note.id)}>Share</button>
                        <button onClick={() => onMakePublic(note.id)}>
                            {note.isPublic ? 'Public' : 'Make Public'}
                        </button>
                        <button onClick={() => onDeleteNote(note.id)}>Delete</button>
                    </div>
                </div>
            ))}

            {sharedNotes.length > 0 && (
                <>
                    <h2>Shared With Me</h2>
                    {sharedNotes.map(note => (
                        <div
                            key={note.id}
                            className={`note-item shared ${selectedNote?.id === note.id ? 'selected' : ''}`}
                            onClick={() => onNoteSelect(note)}
                        >
                            <div className="note-title">{note.title}</div>
                            <div className="note-info">
                                Shared by: {note.user?.username}
                            </div>
                        </div>
                    ))}
                </>
            )}

            {shareDialogNoteId && (
                <ShareNoteDialog
                    noteId={shareDialogNoteId}
                    onClose={() => setShareDialogNoteId(null)}
                />
            )}
        </div>
    );
};
