import React, { useState, useEffect } from 'react';
import { User } from '../types/Note';
import { api } from '../services/api';
import './ShareNoteDialog.css';

interface ShareNoteDialogProps {
    noteId: number;
    onShare: (userId: number) => void;
    onClose: () => void;
}

export const ShareNoteDialog: React.FC<ShareNoteDialogProps> = ({ noteId, onShare, onClose }) => {
    const [searchTerm, setSearchTerm] = useState('');
    const [users, setUsers] = useState<User[]>([]);
    const [isLoading, setIsLoading] = useState(false);
    const [error, setError] = useState<string | null>(null);

    useEffect(() => {
        const searchUsers = async () => {
            if (!searchTerm.trim()) {
                setUsers([]);
                return;
            }

            try {
                setIsLoading(true);
                setError(null);
                const results = await api.searchUsers(searchTerm);
                setUsers(results);
            } catch (err) {
                setError('Failed to search users');
                console.error('Error searching users:', err);
            } finally {
                setIsLoading(false);
            }
        };

        const debounceTimer = setTimeout(searchUsers, 300);
        return () => clearTimeout(debounceTimer);
    }, [searchTerm]);

    return (
        <div className="share-dialog">
            <h2>Share Note</h2>
            <div className="search-container">
                <input
                    type="text"
                    placeholder="Search users..."
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                    className="search-input"
                />
            </div>
            <div className="users-list">
                {isLoading && <div className="loading">Searching users...</div>}
                {error && <div className="error">{error}</div>}
                {!isLoading && !error && users.length === 0 && searchTerm && (
                    <div className="no-results">No users found</div>
                )}
                {users.map(user => (
                    <div key={user.id} className="user-item">
                        <span>{user.username}</span>
                        <button onClick={() => onShare(user.id)}>Share</button>
                    </div>
                ))}
            </div>
            <div className="dialog-actions">
                <button onClick={onClose} className="cancel-button">Cancel</button>
            </div>
        </div>
    );
};
