import React, { useState, useEffect, useCallback } from 'react';
import { useNavigate } from 'react-router-dom';
import { Notification } from '../services/notificationService';
import { notificationService } from '../services/notificationService';
import { signalRService } from '../services/signalRService';
import './NotificationBell.css';

const NotificationBell: React.FC = () => {
    const [notifications, setNotifications] = useState<Notification[]>([]);
    const [showDropdown, setShowDropdown] = useState(false);
    const navigate = useNavigate();

    const loadNotifications = useCallback(async () => {
        try {
            console.log('Loading notifications...');
            const data = await notificationService.getNotifications();
            console.log('Loaded notifications:', data);
            setNotifications(data);
        } catch (error) {
            console.error('Failed to load notifications:', error);
        }
    }, []);

    useEffect(() => {
        console.log('Setting up notification listeners');
        loadNotifications();
        
        const setupNotifications = async () => {
            try {
                if (!signalRService.isConnected()) {
                    console.log('SignalR not connected, connecting...');
                    await signalRService.startConnection();
                }

                signalRService.onNotification((notification: Notification) => {
                    console.log('NotificationBell received notification:', notification);
                    setNotifications(prev => {
                        if (prev.some(n => n.id === notification.id)) {
                            console.log('Notification already exists, skipping');
                            return prev;
                        }
                        console.log('Adding new notification');
                        return [notification, ...prev];
                    });
                });
            } catch (error) {
                console.error('Error setting up notifications:', error);
            }
        };

        setupNotifications();

        const interval = setInterval(loadNotifications, 300000);
        
        return () => {
            console.log('Cleaning up notification listeners');
            clearInterval(interval);
            signalRService.offNotification();
        };
    }, [loadNotifications]);

    const handleNotificationClick = async (notification: Notification) => {
        try {
            await notificationService.markAsRead(notification.id);
            setShowDropdown(false);

            // Navigate based on notification type
            if (notification.type === 'NewPublicNote') {
                navigate('/notes/public');
            } else if (notification.type === 'NoteShared') {
                navigate('/notes/shared');
            }
            
            await loadNotifications(); // Refresh notifications list
        } catch (error) {
            console.error('Error handling notification click:', error);
        }
    };

    const handleMarkAllAsRead = async () => {
        await notificationService.markAllAsRead();
        loadNotifications();
    };

    const unreadCount = notifications.filter(n => !n.isRead).length;

    return (
        <div className="notification-bell">
            <button 
                className="bell-button" 
                onClick={() => setShowDropdown(!showDropdown)}
            >
                🔔
                {unreadCount > 0 && (
                    <span className="notification-badge">{unreadCount}</span>
                )}
            </button>

            {showDropdown && (
                <div className="notification-dropdown">
                    <div className="notification-header">
                        <h3>Notifications</h3>
                        {unreadCount > 0 && (
                            <button 
                                className="mark-all-read"
                                onClick={handleMarkAllAsRead}
                            >
                                Mark all as read
                            </button>
                        )}
                    </div>
                    <div className="notification-list">
                        {notifications.length === 0 ? (
                            <div className="no-notifications">
                                No notifications
                            </div>
                        ) : (
                            notifications.map(notification => (
                                <div
                                    key={`${notification.id}-${notification.type}`}
                                    className={`notification-item ${!notification.isRead ? 'unread' : ''}`}
                                    onClick={() => handleNotificationClick(notification)}
                                >
                                    <div className="notification-content">
                                        <p>{notification.message}</p>
                                        <span className="notification-time">
                                            {new Date(notification.createdAt).toLocaleString()}
                                        </span>
                                    </div>
                                </div>
                            ))
                        )}
                    </div>
                </div>
            )}
        </div>
    );
};

export default NotificationBell;
