import React, { useState, useEffect, useCallback } from 'react';
import { useNavigate } from 'react-router-dom';
import { Notification } from '../services/notificationService';
import { notificationService } from '../services/notificationService';
import { signalRService } from '../services/signalRService';
import './NotificationBell.css';

const NotificationBell: React.FC = () => {
    const [notifications, setNotifications] = useState<Notification[]>([]);
    const [showDropdown, setShowDropdown] = useState(false);
    const navigate = useNavigate();

    const loadNotifications = useCallback(async () => {
        try {
            const data = await notificationService.getNotifications();
            setNotifications(data);
        } catch (error) {
            console.error('Failed to load notifications:', error);
        }
    }, []);

    useEffect(() => {
        loadNotifications();
        
        // Set up real-time notification handling
        if (signalRService.hubConnection) {
            signalRService.hubConnection.on('ReceiveNotification', (notification: Notification) => {
                setNotifications(prev => [notification, ...prev]);
            });
        }

        // Poll for new notifications every minute as backup
        const interval = setInterval(loadNotifications, 60000);
        
        return () => {
            clearInterval(interval);
            if (signalRService.hubConnection) {
                signalRService.hubConnection.off('ReceiveNotification');
            }
        };
    }, [loadNotifications]);

    const handleNotificationClick = async (notification: Notification) => {
        await notificationService.markAsRead(notification.id);
        if (notification.noteId) {
            navigate(`/notes/${notification.noteId}`);
        }
        setShowDropdown(false);
        loadNotifications();
    };

    const handleMarkAllAsRead = async () => {
        await notificationService.markAllAsRead();
        loadNotifications();
    };

    const unreadCount = notifications.filter(n => !n.isRead).length;

    return (
        <div className="notification-bell">
            <button 
                className="bell-button" 
                onClick={() => setShowDropdown(!showDropdown)}
            >
                ðŸ””
                {unreadCount > 0 && (
                    <span className="notification-badge">{unreadCount}</span>
                )}
            </button>

            {showDropdown && (
                <div className="notification-dropdown">
                    <div className="notification-header">
                        <h3>Notifications</h3>
                        {unreadCount > 0 && (
                            <button 
                                className="mark-all-read"
                                onClick={handleMarkAllAsRead}
                            >
                                Mark all as read
                            </button>
                        )}
                    </div>
                    <div className="notification-list">
                        {notifications.length === 0 ? (
                            <div className="no-notifications">
                                No notifications
                            </div>
                        ) : (
                            notifications.map(notification => (
                                <div
                                    key={notification.id}
                                    className={`notification-item ${!notification.isRead ? 'unread' : ''}`}
                                    onClick={() => handleNotificationClick(notification)}
                                >
                                    <div className="notification-content">
                                        <p>{notification.message}</p>
                                        <span className="notification-time">
                                            {new Date(notification.createdAt).toLocaleString()}
                                        </span>
                                    </div>
                                </div>
                            ))
                        )}
                    </div>
                </div>
            )}
        </div>
    );
};

export default NotificationBell;
