import { 
    HubConnectionBuilder, 
    HubConnection,
    HttpTransportType,
    IHttpConnectionOptions 
} from '@microsoft/signalr';
import { getCurrentUser } from './api';

const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';

class SignalRService {
    private connection: HubConnection | null = null;
    private currentNoteId: number | null = null;
    private handlers: { [key: string]: (...args: any[]) => void } = {};

    startConnection = async () => {
        try {
            const user = getCurrentUser();
            if (!user?.Id) {
                console.log('No user found, skipping SignalR connection');
                return;
            }

            const hubUrl = `${API_URL}/notehub`;
            console.log('Connecting to SignalR hub at:', hubUrl);

            const options: IHttpConnectionOptions = {
                withCredentials: true,
                headers: {
                    'UserId': user.Id.toString()
                },
                transport: HttpTransportType.WebSockets
            };

            this.connection = new HubConnectionBuilder()
                .withUrl(hubUrl, options)
                .withAutomaticReconnect()
                .build();

            if (this.connection) {
                this.connection.on('Reconnecting', () => {
                    console.log('Attempting to reconnect to SignalR hub');
                });

                this.connection.on('Reconnected', (connectionId?: string) => {
                    console.log('Reconnected to SignalR hub with ID:', connectionId);
                });

                this.connection.on('Closed', () => {
                    console.log('SignalR Connection closed');
                });

                await this.connection.start();
                console.log('SignalR Connected successfully!');
            }

        } catch (err) {
            console.error('SignalR Connection Error:', err);
        }
    };

    stopConnection = async () => {
        try {
            if (this.connection) {
                await this.connection.stop();
                this.removeAllHandlers();
                console.log('SignalR Disconnected');
            }
        } catch (err) {
            console.error('Error stopping SignalR connection:', err);
        }
    };

    isConnected = (): boolean => {
        return this.connection?.state === 'Connected';
    };

    public async joinNote(noteId: number): Promise<void> {
        if (this.currentNoteId) {
            await this.leaveNote(this.currentNoteId);
        }
        
        if (this.connection?.state === 'Connected') {
            await this.connection.invoke('JoinNote', noteId);
            this.currentNoteId = noteId;
        }
    }

    public async leaveNote(noteId: number): Promise<void> {
        if (this.connection?.state === 'Connected') {
            await this.connection.invoke('LeaveNote', noteId);
            this.currentNoteId = null;
        }
    }

    public onNoteUpdated(callback: (noteId: number, content: string) => void): void {
        this.handlers['NoteUpdated'] = callback;
        this.connection?.on('NoteUpdated', callback);
    }

    public async updateNote(noteId: number, content: string): Promise<void> {
        if (this.connection?.state === 'Connected') {
            await this.connection.invoke('UpdateNote', noteId, content);
        }
    }

    public onCursorMoved(callback: (username: string, position: number) => void): void {
        this.handlers['CursorMoved'] = callback;
        this.connection?.on('CursorMoved', callback);
    }

    public async updateCursorPosition(noteId: number, position: number): Promise<void> {
        const user = getCurrentUser();
        if (user && this.connection?.state === 'Connected') {
            await this.connection.invoke('CursorMoved', noteId, user.Username, position);
        }
    }

    public onCollaboratorJoined(callback: (username: string) => void): void {
        this.handlers['CollaboratorJoined'] = callback;
        this.connection?.on('CollaboratorJoined', callback);
    }

    public onCollaboratorLeft(callback: (username: string) => void): void {
        this.handlers['CollaboratorLeft'] = callback;
        this.connection?.on('CollaboratorLeft', callback);
    }

    public removeAllHandlers(): void {
        if (this.connection) {
            Object.keys(this.handlers).forEach(event => {
                if (this.connection && this.handlers[event]) {
                    this.connection.off(event);
                }
            });
            this.handlers = {};
        }
    }

    public onUserStartedTyping(callback: (username: string) => void): void {
        this.handlers['UserStartedTyping'] = callback;
        this.connection?.on('UserStartedTyping', callback);
    }

    public onUserStoppedTyping(callback: (username: string) => void): void {
        this.handlers['UserStoppedTyping'] = callback;
        this.connection?.on('UserStoppedTyping', callback);
    }

    public async notifyTypingStarted(noteId: number): Promise<void> {
        if (this.connection?.state === 'Connected') {
            await this.connection.invoke('StartTyping', noteId);
        }
    }

    public async notifyTypingStopped(noteId: number): Promise<void> {
        if (this.connection?.state === 'Connected') {
            await this.connection.invoke('StopTyping', noteId);
        }
    }

    public async saveVersion(noteId: number, content: string, versionName: string): Promise<void> {
        if (this.connection?.state === 'Connected') {
            await this.connection.invoke('SaveVersion', noteId, content, versionName);
        }
    }

    public onVersionSaved(callback: (noteId: number, versionId: string) => void): void {
        this.handlers['VersionSaved'] = callback;
        this.connection?.on('VersionSaved', callback);
    }

    public async updateSelection(noteId: number, selection: { start: number; end: number }): Promise<void> {
        if (this.connection?.state === 'Connected') {
            await this.connection.invoke('UpdateSelection', noteId, selection);
        }
    }

    public onSelectionChanged(callback: (username: string, selection: { start: number; end: number }) => void): void {
        this.handlers['SelectionChanged'] = callback;
        this.connection?.on('SelectionChanged', callback);
    }
}

export const signalRService = new SignalRService();
