import React from 'react';
import { Note, NoteStatus, getNoteStatus } from '../types/Note';
import './NoteList.css';

interface NoteListProps {
    notes: Note[];
    selectedNote?: Note;
    onNoteSelect: (note: Note) => void;
    onDeleteNote: (id: number) => void;
    onMakePublic: (id: number) => void;
    onShare: (id: number) => void;
    viewType?: string;
    isLoading?: boolean;
    error?: string | null;
}

const NoteList: React.FC<NoteListProps> = ({ 
    notes, 
    selectedNote,
    onNoteSelect, 
    onDeleteNote, 
    onMakePublic,
    onShare,
    isLoading,
    error 
}) => {
    // Add debug logging
    console.log('NoteList render:', {
        notesCount: notes.length,
        notes,
        isLoading,
        error
    });

    // Helper function to get status class name
    const getStatusClassName = (status: NoteStatus): string => {
        switch (status) {
            case NoteStatus.Public:
                return 'public';
            case NoteStatus.Shared:
                return 'shared';
            case NoteStatus.Personal:
                return 'personal';
            default:
                return 'personal';
        }
    };

    // Helper function to get status text
    const getStatusText = (status: NoteStatus): string => {
        switch (status) {
            case NoteStatus.Public:
                return 'Public';
            case NoteStatus.Shared:
                return 'Shared';
            case NoteStatus.Personal:
                return 'Personal';
            default:
                return 'Personal';
        }
    };

    if (isLoading) {
        console.log('Loading state');
        return <div>Loading...</div>;
    }
    if (error) {
        console.log('Error state:', error);
        return <div className="error">{error}</div>;
    }
    if (notes.length === 0) {
        console.log('No notes state');
        return <div className="no-notes-message">No notes found</div>;
    }

    return (
        <div className="notes-grid">
            {notes.map((note) => (
                <div 
                    key={note.id} 
                    className={`note-card ${selectedNote?.id === note.id ? 'selected' : ''}`}
                >
                    <div 
                        className="note-content"
                        onClick={() => onNoteSelect(note)}
                    >
                        <div className="note-header">
                            <h3>{note.title}</h3>
                            <span className={`note-status ${getStatusClassName(note.status)}`}>
                                {getStatusText(note.status)}
                            </span>
                        </div>
                        <p>{note.content}</p>
                        <div className="note-metadata">
                            <small>Category: {note.category}</small>
                            {note.owner && <small className="note-owner">Owner: {note.owner}</small>}
                        </div>
                    </div>
                    <div className="note-actions">
                        <button 
                            className="delete-button"
                            onClick={(e) => {
                                e.stopPropagation();
                                onDeleteNote(note.id);
                            }}
                        >
                            Delete
                        </button>
                        <button 
                            className="share-button"
                            onClick={(e) => {
                                e.stopPropagation();
                                onShare(note.id);
                            }}
                        >
                            Share
                        </button>
                        {note.status !== NoteStatus.Public && (
                            <button 
                                className="public-button"
                                onClick={(e) => {
                                    e.stopPropagation();
                                    onMakePublic(note.id);
                                }}
                            >
                                Make Public
                            </button>
                        )}
                    </div>
                </div>
            ))}
        </div>
    );
};

export default NoteList;
